import { createContext, ReactNode, useContext, useState } from "react";

interface Product {
  id: number;
  title: string;
  description: string;
  price: number;
  image: string;
}

interface ProductProps {
  children: ReactNode;
}

interface ProductProviderData {
  product: Product[];
}
const ProductsContext = createContext<ProductProviderData>(
  {} as ProductProviderData
);

export const ProductsProvider = ({ children }: ProductProps) => {
  const [product, setProduct] = useState<Product[]>([]);

  return (
    <ProductsContext.Provider value={{ product }}>
      {children}
    </ProductsContext.Provider>
  );
};

export const useProduct = () => useContext(ProductsContext);
