import styled from "styled-components";

export const HeaderStyled = styled.header`
  width: 95%;
  height: 8vh;
  margin-left: 2.5%;
  margin-top: 1%;
  margin-bottom: 1%;
  background-color: darkblue;
  display: flex;
  justify-content: center;
  h1 {
    color: white;
    font-size: 30px;
    font-weight: bold;
  }
`;
