import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import { useCart } from "../../providers/cart";
import { Button, CardActionArea, CardActions } from "@mui/material";

export const CardItem = ({ image, name, price, description, id, inCart }) => {
  const { addProduct, deleteProduct, cart } = useCart();
  console.log(cart);
  return (
    <Card sx={{ maxWidth: 250 }} key={id}>
      <CardActionArea>
        <CardMedia component="img" height="200" image={image} alt={name} />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            {name}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            descrição {description}
          </Typography>
          <br />
          <Typography variant="body2" color="text.primary">
            preço:{price}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        {inCart === true ? (
          <Button onClick={deleteProduct}>Remover</Button>
        ) : (
          <Button onClick={addProduct}>comprar</Button>
        )}
      </CardActions>
    </Card>
  );
};
