import styled from "styled-components";

export const Container = styled.section`
  max-width: 260px;
  display: flex;
  flex-direction: row;
  margin: 1%;
  box-shadow: (10px, 10px, 10px, 10px, black);
  padding: 2%;
  border-radius: 10px;
  background-color: #f5f5f5;

  img {
    height: 180px;
  }
`;
