import { createContext, ReactNode, useContext, useState } from "react";

interface Product {
  id: number;
  title: string;
  description: string;
  price: number;
  image: string;
}

interface ProductProps {
  children: ReactNode;
}

interface ProductProviderData {
  product: Product[];
}
const ProductsContext = createContext<ProductProviderData>(
  {} as ProductProviderData
);

export const ProductsProvider = ({ children }: ProductProps) => {
  const [product, setProduct] = useState<Product[]>([
    {
      id: 11,
      title: "Samsung Galaxy A01 Dual SIM 32 GB preto 2 GB RAM",
      description:
        "Processador Snapdragon 439 Octa-Core de 2GHz com 2GB de RAM.",
      price: 699.99,
      image:
        "https://http2.mlstatic.com/D_NQ_NP_661764-MLA44282592265_122020-O.webp",
    },
    {
      id: 12,
      title: "Xiaomi Redmi 9A Dual SIM 32 GB azul 2 GB RAM",
      description:
        "Processador MediaTek Helio G25 Octa-Core de 2GHz com 2GB de RAM.",
      price: 799.99,
      image:
        "https://http2.mlstatic.com/D_NQ_NP_958898-MLA43824398273_102020-O.webp",
    },
  ]);

  return (
    <ProductsContext.Provider value={{ product }}>
      {children}
    </ProductsContext.Provider>
  );
};

export const useProduct = () => useContext(ProductsContext);
