import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import { useCart } from "../../providers/cart";
import { Button, CardActionArea, CardActions } from "@mui/material";

export const CardItem = ({ product, inCart }) => {
  const { addProduct, deleteProduct, cart } = useCart();
  // console.log(product);
  return (
    <Card sx={{ maxWidth: 250 }} key={product.id}>
      <CardActionArea>
        <CardMedia
          component="img"
          height="200"
          image={product.image}
          alt={product.name}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            {product.name}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            descrição {product.description}
          </Typography>
          <br />
          <Typography variant="body2" color="text.primary">
            preço:{product.price}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        {inCart === true ? (
          <Button onClick={() => deleteProduct(product)}>Remover</Button>
        ) : (
          <Button onClick={() => addProduct(product)}>compra</Button>
        )}
      </CardActions>
    </Card>
  );
};
